const fileNameKKB = ref('');
const fileSizeKKB = ref('');
const fileKKBId = ref(null);

const isUploadedKKR = ref(false);
const fileNameKKR = ref('');
const fileSizeKKR = ref('');
const fileKKRId = ref(null);

const isUploadedKKF = ref(false);
const fileNameKKF = ref('');
const fileSizeKKF = ref('');
const fileKKFId = ref(null);

const isUploadedKKO = ref(false);
const fileNameKKO = ref('');
const fileSizeKKO = ref('');
const fileKKOId = ref(null);

const isUploadedmitra = ref(false);
const fileNamemitra = ref('');
const fileSizemitra = ref('');
const filemitraId = ref(null);

const isUploadedsurat = ref(false);
const fileNamesurat = ref('');
const fileSizesurat = ref('');
const filesuratId = ref(null);

const isUploadedlainnya = ref(false);
const fileNamelainnya = ref('');
const fileSizelainnya = ref('');
const filelainnyaId = ref(null);

const maxSizeFile = 10 * 1024 * 1024;

watch(
  () => props.data, 
  (newData) => {
    newData?.attachmentsPks?.forEach((item) => {
        if (item.fileType == "KKO") {
            isUploadedKKO.value = true
            fileNameKKO.value = item.fileName
            fileSizeKKO.value = item.fileSize
            fileKKOId.value = item.id
        } else if (item.fileType == "KKF") {
            isUploadedKKF.value = true
            fileNameKKF.value = item.fileName
            fileSizeKKF.value = item.fileSize
            fileKKFId.value = item.id
        } else if (item.fileType == "KKR") {
            isUploadedKKR.value = true
            fileNameKKR.value = item.fileName
            fileSizeKKR.value = item.fileSize
            fileKKRId.value = item.id
        } else if (item.fileType == "KKB") {
            isUploadedKKB.value = true
            fileNameKKB.value = item.fileName
            fileSizeKKB.value = item.fileSize
            fileKKBId.value = item.id
        } else if (item.fileType == "Proposal Mitra") {
            isUploadedmitra.value = true
            fileNamemitra.value = item.fileName
            fileSizemitra.value = item.fileSize
            filemitraId.value = item.id
        } else if (item.fileType == "Dokumen Surat Menyurat") {
            isUploadedsurat.value = true
            fileNamesurat.value = item.fileName
            fileSizesurat.value = item.fileSize
            filesuratId.value = item.id
        } else if (item.fileType == "Dokumen Lainnya") {
            isUploadedlainnya.value = true
            fileNamelainnya.value = item.fileName
            fileSizelainnya.value = item.fileSize
            filelainnyaId.value = item.id
        }
    })
  },
  { immediate: true }
);

function handleFileUploadKKB(event) {
    const file = event.target.files[0];
    if (file) {
        if (file.size > maxSizeFile) {
            return props.onFileSizeOver(`Maaf ukuran file ${(file.size / 1024 / 1024).toFixed(2)} MB melebihi 10 MB`)
        }
        isUploadedKKB.value = true;
        fileNameKKB.value = file.name;
        fileSizeKKB.value = (file.size / 1024 / 1024).toFixed(2) + ' MB'; 
        emit("fileKKB", file);
        if (fileKKBId.value) {
            emit('fileKKBId', fileKKBId.value);
        } else {
            emit('fileKKBId', null);
        }
    }
}
function handleDeleteKKB() {
    isUploadedKKB.value = false;
    emit("fileKKB", null);
    emit('fileKKBId', null);
    if (fileKKBId.value) {
        emit('deleteId', fileKKBId.value);
        fileKKBId.value = null;
    }
}

