    };
  },
  methods: {
    // Popup
    openSend() {
      this.isSendOpen = true;
      this.isOkOpen = false; 
    },
    closeSend() {
      this.isSendOpen = false;
    },
    openOk() {
      this.isOkOpen = true;
      this.isSendOpen = false; 
    },
    closeOk() {
      this.isOkOpen = false;
    },

    // Upload
    // Proposal Mitra
    handleFileUploadmitra(event) {
      const file = event.target.files[0];
      if (file) {
        this.isUploadedmitra = true;
        this.fileNamemitra = file.name;
        this.fileSizemitra = (file.size / 1024 / 1024).toFixed(2) + ' MB'; 
      }
    },
    handleDeletemitra() {
            this.isUploadedmitra = false;
        },

    // Dokumen Surat Menyurat
    handleFileUploadsurat(event) {
      const file = event.target.files[0];
      if (file) {
        this.isUploadedsurat = true;
        this.fileNamesurat = file.name;
        this.fileSizesurat = (file.size / 1024 / 1024).toFixed(2) + ' MB'; 
      }
    },
    handleDeletesurat() {
            this.isUploadedsurat = false;
        },

    // Dokumen Lainnya
    handleFileUploadlainnya(event) {
      const file = event.target.files[0];
      if (file) {
        this.isUploadedlainnya = true;
        this.fileNamelainnya = file.name;
        this.fileSizelainnya = (file.size / 1024 / 1024).toFixed(2) + ' MB'; 
      }
    },
    handleDeletelainnya() {
            this.isUploadedlainnya = false;
        }
  },
};
</script>